import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { addContact, deleteContact, listContact } from '../actions/contactActions';
import {FaTrash} from 'react-icons/fa';
import {RiAddLine} from 'react-icons/ri';

// Component Imports
import ContactCards from '../components/ContactCards';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { useNavigate, useParams } from 'react-router';
import { ADD_CONTACT_RESET, DELETE_CONTACT_RESET } from '../constants/contactConstants';
import { detailsUser } from '../actions/userActions';

const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;
    margin-top: 2%;

    h1 {
        font-size: 40px;
        color: white;
    }
    
`;

const Add = styled.button`
    width: 10%;
    margin: 0 0 1% 25%;
    border: solid black 2px;
    border-radius: 10px;
    cursor: pointer;
    outline: none;
    background-color: green;
    padding: 5px;

    &:hover .add {
        transform: scale(1.1);
        transition: transform ease-in-out 1s;
    }
`;

const ContactRow = styled.div`
    display: flex;
    margin-bottom: 2%;
`;

const Delete = styled.button`
    margin-left: 20px;
    border: solid black 2px;
    border-radius: 10px;
    background-color: red;
    cursor: pointer;
    outline: none;
`;

export default function ContactsPage() {

    const {id} = useParams();

    const userDetails = useSelector(state => state.userDetails);
    const {loading, error, user} = userDetails;

    const dispatch = useDispatch();
    const navigate = useNavigate();

    useEffect(() => {
        dispatch(detailsUser(id));
    }, [dispatch, navigate, id]);

    const addHandler = () => {
        dispatch(addContact());
    }

    const deleteHandler = (contact) => {
        dispatch(deleteContact(contact._id));
    }

    return (
        <PageWrapper>
            <h1>Contacts</h1>
            <Add onClick = {addHandler}><RiAddLine className = 'add' fontSize = '40px'/></Add>
            {loading ? (<LoadingBox />)
                :
            error ? (<MessageBox variant = 'danger'>{error}</MessageBox>)
                :
                (
                <>
                    {contacts.map(contact => (
                        <ContactRow>
                            <ContactCards key = {contact._id} contact = {contact} />
                            <Delete onClick = {() => deleteHandler(contact)}><FaTrash fontSize = '30px' /></Delete>
                        </ContactRow>
                    ))}
                </>
            )}
        </PageWrapper>
    )
}
