import React, { useEffect, useState } from "react";
import { useRef } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import {updateDirection} from '../actions/recipeActions';

export default function IngredientCards(props) {

    const [task, setTask] = useState('');
    const [height, setHeight] = useState('60px');

    const {direction} = props;
    const {recipeId} = props;
    const dispatch = useDispatch();

    useEffect(() => {
        setTask(direction.task);
    }, [direction]);

    const DirectionCard = styled.textarea`
        display: flex;
        justify-content: space-between;
        width: 50%;
        margin-left: 25%;
        border: solid black 2px;
        border-radius: 10px;
        font-size: 20px;
        color: white;
        background-color: rgb(73, 73, 77);
        resize: none;
        padding: 10px;
        height: ${height}px;
        outline: none;
    `;

    const submitHandler = () => {
        dispatch(updateDirection(recipeId, {
            _id: direction._id,
            task
        }));
    }

    const handleChange = (e) => {
        e.preventDefault();

        setTask(e.target.value);
        
    }

    return (
        <DirectionCard onSubmit={submitHandler}>
        <DirectionCard 
            id = {direction._id}
            autoFocus
            type = 'text'
            value = {task}
            onChange = {handleChange}
            onSubmit = {submitHandler}
        />
        <button type = 'submit' hidden />
        </DirectionCard>
    )
}
