import React, {useEffect} from 'react';
import { useDispatch } from 'react-redux';
import styled from 'styled-components';

const PageWrapper = styled.div`

`;

export default function Recipe() {

    const dispatch = useDispatch();

    useEffect(() => {

    }, [dispatch])

    const addHandler = () => {
        dispatch(addContact(id));
    }

    const deleteHandler = (contact) => {
        dispatch(deleteContact(id, contact._id));
    }

    const checker = () => {
        for (var i = 0; i < user.contacts.length; i++) {
            if (user.contacts[i].name === 'default name') {
                var x = true;
                break;
            } else {
                var x = false;
            }
        }

        return x;
    }

    return (
       <PageWrapper>
           <h1>Contacts</h1>
            <Add onClick = {addHandler}><RiAddLine className = 'add' fontSize = '40px'/></Add>
            {loading ? (<LoadingBox />)
                :
            error ? (<MessageBox variant = 'danger'>{error}</MessageBox>)
                :
                (
                <>
                    {checker() === true && <h3>Unfinished:</h3>}
                    {recipes.filter(recipe => recipe.name === 'default name').map(recipe => (
                        <ContactRow>
                            <RecipeCards key = {recipe._id} recipe = {recipe} userId = {id}/>
                            <Delete onClick = {() => deleteHandler(recipe)}><FaTrash fontSize = '30px' /></Delete>
                        </ContactRow>
                    ))}
                    <h3>Completed Contacts:</h3>
                    {user.recipes.filter(recipe => recipe.name !== 'default name').sort((a, b) => a.name.localeCompare(b.name)).map(recipe => (
                        <ContactRow>
                            <ContactCards key = {recipe._id} recipe = {recipe} userId = {id}/>
                            <Delete onClick = {() => deleteHandler(recipe)}><FaTrash fontSize = '30px' /></Delete>
                        </ContactRow>
                    ))}
                </>
            )}
       </PageWrapper>
    )
}
