import React, { useEffect, useState } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { detailsContact } from '../actions/contactAction';
import { useParams } from 'react-router';

const PageWrapper = styled.div`

`;

const ContactEditForm = styled.form`

`;

export default function ContactEdit() {

    const [name, setName] = useState('');
    const [nickName, setNickName] = useState('');
    const [phone, setPhone] = useState('');
    const [email, setEmail] = useState('');
    const [job, setJob] = useState('');
    const [company, setCompany] = useState('');

    const dispatch = useDispatch();

    const {id} = useParams();

    const contactDetails = useSelector(state => state.contactDetails);
    const {loading, error, contact} = contactDetails;

    useEffect(() => {
        dispatch(detailsContact(id));
    }, [dispatch, id])

    return (
        <PageWrapper>
            <ContactEditForm>
                {loading ? (
                    <LoadingBox />
                ) : error ? (
                    <MessageBox variant = 'danger'>{error}</MessageBox>
                ) : (
                    <>
                        <div>
                            <label htmlFor="name">Name: </label>
                            <input 
                                type="text" 
                                id = 'name'
                                value = {name} 
                                onChange = {(e) => setName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="nickName">Nickname: </label>
                            <input 
                                type="text" 
                                id = 'nickName' 
                                value = {nickName} 
                                onChange = {(e) => setNickName(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="phone">Phone: </label>
                            <input 
                                type="text" 
                                id = 'phone' 
                                value = {phone} 
                                onChange = {(e) => setPhone(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="email">Email: </label>
                            <input 
                                type="text" 
                                id = 'email' 
                                value = {email} 
                                onChange = {(e) => setEmail(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="job">Job: </label>
                            <input 
                                type="text" 
                                id = 'job' 
                                value={job} 
                                onChange = {(e) => setJob(e.target.value)}
                            />
                        </div>
                        <div>
                            <label htmlFor="company">Company: </label>
                            <input 
                                type="text" 
                                id = 'company' 
                                value={company} 
                                onChange = {(e) => setCompany(e.target.value)}
                            />
                        </div>
                        <Submit type = 'submit'>Update</Submit>
                    </>
                )}
            </ContactEditForm>
        </PageWrapper>
    )
}
