import React, { useEffect, useState } from "react";
import styled from "styled-components";



export default function IngredientCards(props) {

    const [task, setTask] = useState('');

    const {direction} = props;

    useEffect(() => {
        setTask(direction.task);
    }, [direction]);

    const setHeight = (id) => {
        document.getElementById(id).style.height = document.getElementById(id).scrollHeight+'px';
    }

    

    return (
        <DirectionCard 
            key = {direction._id}
            id = {direction._id}
            value = {task}
            onChange = {(e) => setTask(e.value.target)}
        >
            
        </DirectionCard>
    )
}
