import React, { useEffect, useState } from "react";
import { useRef } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import {updateDirection} from '../actions/recipeActions';

const DirectionCard = styled.form`
    display: flex;
    justify-content: space-between;
    width: 50%;
    margin-left: 25%;
    border: solid black 2px;
    border-radius: 10px;
    font-size: 20px;
    color: white;
    background-color: rgb(73, 73, 77);
    resize: none;
    padding: 10px;
    height: fit-content;
    outline: none;

    textarea {
        width: 100%;
        border: none;
        font-size: 20px;
        color: white;
        background-color: rgb(73, 73, 77);
        resize: none;
        padding: 10px;
        height: ${props = props.height || '100px'};
        outline: none;
    }
`;

export default function IngredientCards(props) {

    const [task, setTask] = useState('');
    const [height, setHeight] = useState('60px');

    const {direction} = props;
    const {recipeId} = props;
    const dispatch = useDispatch();

    useEffect(() => {
        setTask(direction.task);
        setHeight(direction.task.length / 2);
    }, [direction]);

    

    const submitHandler = () => {
        dispatch(updateDirection(recipeId, {
            _id: direction._id,
            task
        }));
    }

    return (
        <DirectionCard onSubmit={submitHandler}>
            <textarea
                id = {direction._id}
                autoFocus
                height = {height}
                value = {task}
                onChange = {(e) => setTask(e.target.value)}
            />
            <button type = 'submit' hidden />
        </DirectionCard>
    )
}
