import React, {useEffect, useState} from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate, useParams } from 'react-router';
import styled from 'styled-components';
import { detailsUser } from '../actions/userActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

const PageWrapper = styled.div`

`;

const UserEditForm = styled.form`

`;

export default function UserEdit() {

    const dispatch = useDispatch();
    const navigate = useNavigate();
    const {id} = useParams();

    const detailsUser = useSelector(state => state.detailsUser);
    const {loading: loadingDetail, error: errorDetail, user} = detailsUser;

    const [firstName, setFirstName] = useState('');
    const [lastName, setLastName] = useState('');
    const [userName, setUserName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');

    useEffect(() => {
        dispatch(detailsUser(id));
    }, [dispatch]);

    const submitHandler = () => {

    }

    return (
       <PageWrapper>
           <UserEditForm onSubmit = {submitHandler}>
                {loadingDetail ? (
                        <LoadingBox />
                    ) : errorDetail ? (
                        <MessageBox variant = 'danger'>{errorDetail}</MessageBox>
                    ) :
                        <>
                            <h1>{user.name}</h1>
                            <div>
                                <label htmlFor="firstName">First Name: </label>
                                <input 
                                    type="text" 
                                    id = 'firstName'
                                    value = {firstName} 
                                    onChange = {(e) => setFirstName(e.target.value)}
                                />
                            </div>
                            <div>
                                <label htmlFor="lastName">Last Name: </label>
                                <input 
                                    type="text" 
                                    id = 'lastName' 
                                    value = {lastName} 
                                    onChange = {(e) => setLastName(e.target.value)}
                                />
                            </div>
                            <div>
                                <label htmlFor="userName">Username: </label>
                                <input 
                                    type="text" 
                                    id = 'userName' 
                                    value = {userName} 
                                    onChange = {(e) => setUserName(e.target.value)}
                                />
                            </div>
                            <div>
                                <label htmlFor="email">Email: </label>
                                <input 
                                    type="email" 
                                    id = 'email' 
                                    value = {email} 
                                    onChange = {(e) => setEmail(e.target.value)}
                                />
                            </div>
                            <div>
                                <label htmlFor="password">Password: </label>
                                <input 
                                    type="password" 
                                    id = 'password' 
                                    value={password} 
                                    onChange = {(e) => setPassword(e.target.value)}
                                />
                            </div>
                            <div>
                                <label htmlFor="confirmPassword">Confirm Password: </label>
                                <input 
                                    type="password" 
                                    id = 'confirmPassword' 
                                    value={confirmPassword} 
                                    onChange = {(e) => setConfirmPassword(e.target.value)}
                                />
                            </div>
                            <Submit type = 'submit'>Update</Submit>
                        </>
                }
            </UserEditForm>
       </PageWrapper>
    )
}
