import React, { useEffect, useState } from "react";
import { useRef } from "react";
import { useDispatch } from "react-redux";
import styled from "styled-components";
import {updateDirection} from '../actions/recipeActions';

export default function IngredientCards(props) {

    const [task, setTask] = useState('');
    const [height, setHeight] = useState('');
    const textarea = useRef(null);

    const {direction} = props;
    const {recipeId} = props;
    const dispatch = useDispatch();

    const Height = (id) => {
        document.getElementById(id).style.height = document.getElementById(id).scrollHeight+'px';
    }

    

    useEffect(() => {
        setTask(direction.task);
        console.log(textarea.scrollHeight);
    }, [direction]);

    const DirectionCard = styled.textarea`
        display: flex;
        justify-content: space-between;
        width: 50%;
        margin-left: 25%;
        border: solid black 2px;
        border-radius: 10px;
        font-size: 20px;
        color: white;
        background-color: rgb(73, 73, 77);
        resize: none;
        padding: 10px;
        height: auto;
    `;

    const submitHandler = () => {
        dispatch(updateDirection(recipeId, {
            _id: direction._id,
            task
        }));
    }

    return (
        <DirectionCard 
            key = {direction._id}
            id = {direction._id}
            value = {task}
            onChange = {(e) => setTask(e.target.value)}
        />
    )
}
