import React, { useEffect } from 'react';
import { RiAddLine } from 'react-icons/ri';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { listTodo } from '../actions/todoActions';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import TodoItemCard from '../components/todoItemCard';

const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;
    margin-top: 2%;

    h1 {
        font-size: 40px;
        color: white;
    }
    
`;

const Add = styled.button`
    width: 10%;
    margin: 0 0 1% 25%;
    border: solid black 2px;
    border-radius: 10px;
    cursor: pointer;
    outline: none;
    background-color: green;
    padding: 5px;

    &:hover .add {
        transform: scale(1.1);
        transition: transform ease-in-out 1s;
    }
`;

export default function GroupToDo() {

    const todoList = useSelector(state => state.todoList);
    const {loading, error, items} = todoList;

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(listTodo());
    }, [dispatch])

    const addHandler = () => {

    }

    return (
        <PageWrapper>
            <h1>Group To-Do-List</h1>
            <Add onClick = {addHandler}><RiAddLine className = 'add' fontSize = '40px'/></Add>
            {loading ? (
                <LoadingBox />
            ) : error ? (
                <MessageBox variant = 'danger'></MessageBox>
            ) : (
                <>
                    {items.map((item) => (
                        <>
                            <TodoItemCard key = {item._id} item = {item} />
                            <button >Done</button>
                        </>
                    ))}
                </>
            )}
        </PageWrapper>
    )
}
