import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useDispatch, useSelector } from 'react-redux';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';
import { detailsContact } from '../actions/contactAction';

const PageWrapper = styled.div`

`;

export default function ContactEdit( props ) {

    const dispatch = useDispatch();

    const contactId = props.match.params.id;

    const contactDetails = useSelector(state => state.contactDetails);
    const {loading, error, contact} = contactDetails;

    useEffect(() => {
        dispatch(detailsContact(contactId));
    }, [dispatch, contactId])

    return (
        <PageWrapper>
            {loading ? (
                <LoadingBox />
            ) : error ? (
                <MessageBox variant = 'danger'>{error}</MessageBox>
            ) : (
                <>
                    {contact.name}
                </>
            )}
        </PageWrapper>
    )
}
