import React from "react";
import { useNavigate } from "react-router";
import styled from "styled-components";

const IngredientCard = styled.button`
    display: flex;
    justify-content: space-between;
    width: 50%;
    margin-left: 25%;
    border: solid black 2px;
    border-radius: 10px;
    font-size: 20px;
    text-decoration: none;
    color: white;
    background-color: rgb(73, 73, 77); 
    cursor: pointer;

    p {
        padding: 0 30px 0 30px;
    }
`;

const Update = styled.button`
    margin-left: 20px;
    border: solid black 2px;
    border-radius: 10px;
    cursor: pointer;
    outline: none;
    background-color: green;
`;

export default function IngredientCards(props) {

    const [name, setName] = useState('');
    const [quantity, setQuantity] = useState('');
    const [metric, setMetric] = useState('');

    const {ingredient} = props;
    const {recipeId} = props;
    const navigate = useNavigate();

    return (
        <>
            <IngredientCard key = {ingredient._id} onClick = {() => navigate(`/ingredient/${recipeId}/${ingredient._id}`)}>
                <input 
                    type="text" 
                    id = 'name'
                    value = {name}
                    onChange = {() => setName(e.target.value)}
                />
                <p>{ingredient.quantity} {ingredient.metric}</p>
            </IngredientCard>
            <Update onClick = {() => updateHandler(direction)}><GrUpdate fontSize = '30px' /></Update>
        </>
    )
}
