import React, { useEffect, useState } from "react";
import styled from "styled-components";

export default function IngredientCards(props) {

    const [task, setTask] = useState('');
    const [height, setHeight] = useState('');

    const {direction} = props;

    const Height = (id) => {
        document.getElementById(id).style.height = document.getElementById(id).scrollHeight+'px';
    }

    useEffect(() => {
        setTask(direction.task);
        setHeight(Height(direction._id));
    }, [direction, Height]);

    const DirectionCard = styled.textarea`
        display: flex;
        justify-content: space-between;
        width: 50%;
        margin-left: 25%;
        border: solid black 2px;
        border-radius: 10px;
        font-size: 20px;
        color: white;
        background-color: rgb(73, 73, 77);
        resize: none;
        padding: 10px;
    `;

    return (
        <>
            {}
            <DirectionCard 
                key = {direction._id}
                id = {direction._id}
                value = {task}
                onChange = {(e) => setTask(e.value.target)}
            >
                
            </DirectionCard>
        </>
    )
}
