import React, { useState } from 'react';

export default function Timer() {

    const [time, setTime] = useState(0);

    countRef.current = setInterval(() => {
        setTime((time) => time + 1);
    }, 1000);

    const handleReset = () => {
        setInterval(countRef.current);
        setTime(0)
    }

    return (
        <div>
            {time}
            
        </div>
    )
}
