import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import styled from 'styled-components';
import { listContact } from '../actions/contactAction';
import {FaTrash} from 'react-icons/fa';
import {RiAddLine} from 'react-icons/ri';

// Component Imports
import ContactCards from '../components/ContactCards';
import LoadingBox from '../components/LoadingBox';
import MessageBox from '../components/MessageBox';

const PageWrapper = styled.div`
    display: flex;
    flex-direction: column;
    text-align: center;
    margin-top: 2%;

    div {
        h1 {
        font-size: 40px;
        color: white;
    }
    }

    
`;

const Add = styled.button`

`;

const ContactRow = styled.div`
    display: flex;
`;

const Delete = styled.button`
    margin-left: 20px;
    border: solid black 2px;
    border-radius: 10px;
    background-color: red;
    cursor: pointer;
    outline: none;

    &:hover .delete {
        transform: scale(1.1);
        transition: transform ease-in-out 1s;
    }
`;

export default function ContactsPage() {

    const contactList = useSelector(state => state.contactList);
    const {loading, error, contacts} = contactList;

    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(listContact());
    }, [dispatch]);

    return (
        <PageWrapper>
            <div>
                <h1>Contacts</h1>
                <Add><RiAddLine /></Add>
            </div>
            {loading ? (<LoadingBox />)
                :
            error ? (<MessageBox variant = 'danger'>{error}</MessageBox>)
                :
                (
                <>
                    {contacts.map(contact => (
                        <ContactRow>
                            <ContactCards key = {contact._id} contact = {contact} />
                            <Delete><FaTrash className = 'delete' fontSize = '30px' /></Delete>
                        </ContactRow>
                    ))}
                </>
            )}
        </PageWrapper>
    )
}
